    //EVALUA SI EL USUARIO ES PREMIUM O NO
    private void toggleFullScreen() {
        if (!isFullScreen) {
            if (player != null) {
                // Guardar la posiciÃ³n actual del video
                playbackPosition = player.getCurrentPosition();
                playWhenReady = player.getPlayWhenReady();

                // Guardar la posiciÃ³n de reproducciÃ³n en Prefs
                Prefs prefs = new Prefs(this);  // AsegÃºrate de que el contexto sea el correcto
                prefs.setLong("playbackPosition", playbackPosition);
                Log.d("FullScreenToggle", "Saved playback position: " + playbackPosition);
            }

            video_player.player.stop();

            openFullScreenDialog();
            setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_LANDSCAPE);
            isFullScreen = true;
        } else {
            // Salir del modo pantalla completa
            closeFullScreenDialog();
            setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);
            isFullScreen = false;

            if (player != null) {
                // Restaurar el estado de reproducciÃ³n del video
                player.seekTo(playbackPosition);
                player.setPlayWhenReady(playWhenReady);
            }
        }
    }

    // MÃ©todo para abrir el diÃ¡logo de pantalla completa
    private void openFullScreenDialog() {
        if (player != null) {
            playbackPosition = player.getCurrentPosition();
            playWhenReady = player.getPlayWhenReady();

            player.stop();
        }

        FullScreenVideoFragment fullScreenFragment = new FullScreenVideoFragment();
        // Pasar la posiciÃ³n y estado de reproducciÃ³n al fragmento si es necesario
        Bundle args = new Bundle();
        args.putLong("position", playbackPosition);
        args.putBoolean("playWhenReady", playWhenReady);
        fullScreenFragment.setArguments(args);

        getSupportFragmentManager().beginTransaction()
                .replace(android.R.id.content, fullScreenFragment)
                .addToBackStack(null)
                .commit();

    }
    private void closeFullScreenDialog() {
        getSupportFragmentManager().popBackStack();
        // Restaura el estado del reproductor si es necesario
        if (player != null) {
            playbackPosition = player.getCurrentPosition();
            playWhenReady = player.getPlayWhenReady();

            // Pausar el video antes de abrir la pantalla completa
            //player.play();
            video_player.player.play();
        }

    }

    @Override
    public void onFragmentDismissed(long playbackPosition, boolean playWhenReady) {
        player.play();
    }
    @Override
    public Player getSharedPlayer() {
        return player;
    }
    @Override
    protected void onStart() {
        super.onStart();
        if (player != null) {
            player.setPlayWhenReady(playWhenReady);
            player.seekTo(currentWindow, playbackPosition);
        }
    }

    @Override
    protected void onStop() {
        super.onStop();
        if (player != null) {
            playWhenReady = player.getPlayWhenReady();
            playbackPosition = player.getCurrentPosition();
            currentWindow = player.getCurrentWindowIndex();
            player.release();
            player = null;
        }
    }

    @Override
    protected void onSaveInstanceState(Bundle outState) {
        super.onSaveInstanceState(outState);
        if (player != null) {
            outState.putBoolean("playWhenReady", player.getPlayWhenReady());
            outState.putInt("currentWindow", player.getCurrentWindowIndex());
            outState.putLong("playbackPosition", player.getCurrentPosition());
        }
    }

    @Override
    protected void onRestoreInstanceState(Bundle savedInstanceState) {
        super.onRestoreInstanceState(savedInstanceState);
        if (savedInstanceState != null) {
            playWhenReady = savedInstanceState.getBoolean("playWhenReady");
            currentWindow = savedInstanceState.getInt("currentWindow");
            playbackPosition = savedInstanceState.getLong("playbackPosition");
        }
    }

    @Override
    protected void onPause() {
        super.onPause();
        if (player != null) {
            playWhenReady = player.getPlayWhenReady();
            playbackPosition = player.getCurrentPosition();
            currentWindow = player.getCurrentWindowIndex();
            player.release();
            player = null;
        }
    }
    @Override
    protected void onDestroy() {
        super.onDestroy();
        if (player != null) {
            player.release(); // AsegÃºrate de liberar el player cuando la actividad es destruida
            player = null;
        }
    }


