    private boolean isAnswerCorrect(String answer, String... possibleAnswers) {
        for (String possibleAnswer : possibleAnswers) {
            if (possibleAnswer != null && possibleAnswer.trim().equalsIgnoreCase(answer.trim())) {
                return true;
            }
        }
        return false;
    }

